mapper.select:
SELECT S_NAME "sName" , M_NAME "mName" , M_PRICE "mPrice" , C_ID "cId" , CU_NUM "cuNum" , CA_NUM "caNum" , UPLOADFILE "uploadFileName" FROM MYMENU_VIEW WHERE C_ID=#{cId} AND OP_NAME IS NULL
ORDER BY CU_NUM
ORDER By ${sort}

mapper.insert:

SELECT NVL(MAX(NVL(MY_NUM,0)),0)+1
FROM MY_MENU


INSERT INTO MY_MENU ( MY_NUM
					, C_ID
					, M_NUM
					, CU_NUM
					, RECIPENO
					, CA_NUM
					)
VALUES( #{myNum}
		, #{cId}
		, #{mNum}
		, #{cuNum}
		, #{recipeno}
		, #{caNum}
)
