mapper.select:
SELECT O_NUM "oNum" , C_ID "cId" , O_DATE "oDate" , TOTAL "total" , PAY_METHOD "payMethod" , MILEAGE "mileage" , (select to_char(o_date,'yyyy-MM-dd HH:mm:ss') ||'('|| o_num ||')' from orders corder where orders.o_num =corder.refuse_reason) "refuseReason"
FROM ORDERS
S_ID=#{sId}
AND TRUNC(O_DATE) BETWEEN #{startDate} AND #{endDate}
AND DELIVERY_STATUS IN ('C5')
