mapper.select:
SELECT B.* FROM ( SELECT A.*, ROWNUM RN FROM ( SELECT DISTINCT O.O_DATE , O.O_NUM , M.M_NUM , M.M_NAME , ORD.OP_DNUM , O.TOTAL , O.RECEIPT , O.PAY_METHOD , O.C_ADD , O.C_ADD3 , O.DELIVERY_STATUS FROM ORDERS O LEFT OUTER JOIN ORDER_DETAILS ORD ON O.O_NUM = ORD.O_NUM JOIN MENU M ON ORD.M_NUM = M.M_NUM WHERE O.S_ID=#{sId} AND ORD.CA_NUM NOT IN ('CAOP')
AND TO_CHAR(O.O_DATE,'YYYY-MM-DD') BETWEEN '${startDate}' AND '${endDate}'
AND TO_CHAR(O.O_DATE,'YYYY/MM/DD') BETWEEN TO_CHAR(SYSTIMESTAMP-1,'YYYY/MM/DD') AND TO_CHAR(SYSTIMESTAMP,'YYYY/MM/DD')
AND O.DELIVERY_STATUS != 'C4'
ORDER BY O.O_NUM DESC
) A )B WHERE RN >= #{start} and RN <= #{end}

	
		SELECT QTY.QTY	"oQty" 
				, ORD.O_NUM		"oNum"
				, ORD.M_NUM		"mNum"
				, R.OP_NAME		"opName"
		FROM ORDER_DETAILS ORD JOIN RECIPE R
		ON ORD.RECIPENO = R.RECIPENO
		JOIN (
		    SELECT M_NUM,MAX(O_QTY) "QTY"
		  FROM ORDER_DETAILS
		  WHERE O_NUM = #{oNum}
		        AND M_NUM = #{mNum}
		        AND OP_DNUM = #{opDnum}
		  GROUP BY M_NUM
		)QTY
		on QTY.M_NUM = ORD.M_NUM
		WHERE ORD.O_NUM = #{oNum}
		     AND ORD.M_NUM = #{mNum}
		     AND OP_DNUM = #{opDnum}
		    AND ORD.CA_NUM IN ('CAIC','CAHT','CADR','CADE','CAJP')

	SELECT MAX(O_QTY) "oQty"
	FROM ORDER_DETAILS
	WHERE O_NUM =#{mNum}
	      AND M_NUM =#{mNum}
	GROUP BY M_NUM

mapper.update:
UPDATE ORDERS
SET REFUSE_REASON = #{refuseReason} 
	, DELIVERY_STATUS = 'C4'
WHERE O_NUM = #{oNum}


	SELECT (TOTAL*0.01*-1) as addcanclemileage
			, MILEAGE as addmileage
			, S_ID as sId
			, C_ID as cId
	FROM ORDERS
	WHERE O_NUM = #{oNum}

	UPDATE RESERVE SET MILEAGE = MILEAGE + #{addmileage} + #{addcanclemileage}
	WHERE S_ID= #{sId} 
		AND C_ID=#{cId}

UPDATE ORDERS
	SET DE_APPLY_TIME= systimestamp
	    , TAKE_TIME = #{takeTime}
	     , DELIVERY_STATUS='C1'
	WHERE O_NUM= #{oNum}

		
			UPDATE ORDERS
			SET DELIVERY_STATUS = 'C3'
			WHERE O_NUM = #{oNum}
